const csFirst = $input.item.json.cs_first || '';
const zdFirst = $input.item.json.zd_first || '';
const csLast = $input.item.json.converted_last || '';
const zdLast = $input.item.json.zd_last || '';
const csStreet = $input.item.json.cs_street || '';
const zdStreet = $input.item.json.converted_street || '';
const csHouseNo = $input.item.json.cs_house_no || '';
const zdHouseNo = $input.item.json.converted_house_no || '';
const csZip = $input.item.json.cs_zip || '';
const zdZip = $input.item.json.zd_zip || '';
const csCity = $input.item.json.cs_city || '';
const zdCity = $input.item.json.zd_city || '';
const csCountry = $input.item.json.converted_country || '';
const zdCountry = $input.item.json.zd_country || '';
const csCompany = $input.item.json.cs_company_name || '';
const zdCompany = $input.item.json.zd_company_name || '';
const csMobile = $input.item.json.cs_mobile || '';
const zdMobile = $input.item.json.zd_mobile || '';
const csPhone = $input.item.json.cs_phone || '';
const zdPhone = $input.item.json.zd_phone || '';
const csType = $input.item.json.cs_type || '';
const zdType = $input.item.json.zd_type || '';

const choiceFirst = (!csFirst && !zdFirst) ? 'empty'
  : csFirst === zdFirst ? 'identical'
  : !csFirst ? 'ZD'
  : !zdFirst ? 'CS'
  : (csFirst.length <= 2 && csFirst.length < zdFirst.length) ? 'ZD'
  : (zdFirst.length <= 2 && zdFirst.length < csFirst.length) ? 'CS'
  : 'unknown';

const choiceLast = (!csLast && !zdLast) ? 'empty'
  : csLast === zdLast ? 'identical'
  : !csLast ? 'ZD'
  : !zdLast ? 'CS'
  : (csLast.length <= 2 && csLast.length < zdLast.length) ? 'ZD'
  : (zdLast.length <= 2 && zdLast.length < csLast.length) ? 'CS'
  : 'unknown';

const choiceStreet = (!csStreet && !zdStreet) ? 'empty' 
  : csStreet === zdStreet ? 'identical'
  : !csStreet ? 'ZD'
  : !zdStreet ? 'CS'
  : 'unknown';

const choiceHouseNo = (!csHouseNo && !zdHouseNo) ? 'empty' 
  : csHouseNo === zdHouseNo ? 'identical'
  : !csHouseNo ? 'ZD'
  : !zdHouseNo ? 'CS'
  : 'unknown';

const choiceZip = (!csZip && !zdZip) ? 'empty' 
  : csZip === zdZip ? 'identical'
  : !csZip ? 'ZD'
  : !zdZip ? 'CS'
  : 'unknown';

const choiceCity = (!csCity && !zdCity) ? 'empty' 
  : csCity === zdCity ? 'identical'
  : !csCity ? 'ZD'
  : !zdCity ? 'CS'
  : 'unknown';

const choiceCountry = (csCountry === 'UNKNOWN') ? 'unknown'
  : (!csCountry && !zdCountry) ? 'empty' 
  : csCountry === zdCountry ? 'identical'
  : !csCountry ? 'ZD'
  : !zdCountry ? 'CS'
  : 'unknown';

const choiceCompany = (!csCompany && !zdCompany) ? 'empty' 
  : csCompany === zdCompany ? 'identical'
  : !csCompany ? 'ZD'
  : !zdCompany ? 'CS'
  : 'unknown';

const choiceMobile = (!csMobile && !zdMobile) ? 'empty'
  : csMobile === zdMobile ? 'identical'
  : !csMobile ? 'ZD'
  : !zdMobile ? 'CS'
  : 'unknown';

const choicePhone = (!csPhone && !zdPhone) ? 'empty'
  : csPhone === zdPhone ? 'identical'
  : !csPhone ? 'ZD'
  : !zdPhone ? 'CS'
  : 'unknown';

const choiceType = (!csType && !zdType) ? 'empty'
  : csType === zdType ? 'identical'
  : 'unknown';

const csSwapPhones = csPhone && zdMobile ? csPhone === zdMobile : false;

return {
  json: {
    Id: $input.item.json.Id,
    choice_first: choiceFirst,
    choice_last: choiceLast,
    choice_street: choiceStreet,
    choice_house_no: choiceHouseNo,
    choice_zip: choiceZip,
    choice_city: choiceCity,
    choice_country: choiceCountry,
    choice_company: choiceCompany,
    choice_mobile: choiceMobile,
    choice_phone: choicePhone,
    choice_type: choiceType,
    cs_swap_phones: csSwapPhones
  }
};

